import { useState } from "react";
import { useRef } from "react";

function App() {
  const [st, setSt] = useState('text');

  function click(){
    setSt(st+'!');
  }

  return <>
    <p>{st}</p>
    <div>
      <button onClick={()=>click()}>add !</button>
    </div>
  </>
}

export default App;
