import React, { useState } from "react";
import { useEffect } from "react";

function App() {
  const [color, setColor] = useState('red');


  function handleEnter(e) {
    if (e.keyCode === 13) {
      setColor('green')
    }
  }

  useEffect(() => {
    window.addEventListener('keydown', handleEnter);
    document.body.style.backgroundColor = color;
  }, [color])

}

export default App;
