import { useState } from "react";
import { useEffect } from "react";

function App() {
  const [state, setState] = useState(false);

  function click(e) {
    setState(true)
  }

  const block = <div>
    <h1>THIS IS BLOCK</h1>
  </div>;

  useEffect(() => {
    window.addEventListener('click', () => setState(false));
    return ()=>{
      window.removeEventListener('click', ()=> setState(false));
    }
  })

  console.log(state)

  return <>
    <a href='#' onClick={(e)=>click}>click</a><br />
    {state ? block : ''}
  </>

}

export default App;
