import { useRef } from "react";
import './styles.css'
import style from'./style.module.css'

function App() {
  const windowRef = useRef(window);
  const [point, setPoint] = useState(0);


  useEffect(() => {
    const windRef = windowRef.current;

    function scrollListner() {
      setPoint(windRef.scrollY)
    }

    windrefaddEventListener('scroll', scrollListner);

    return ()=> windowRef.current.removeEventListener('scroll', scrollListner)
  }, [])

  console.log(point)


