import { useEffect, useState } from "react";
import { useRef } from "react";
import 'st'

function App() {
  const windowRef = useRef(window);
  const [point, setPoint] = useState(0);


  useEffect(()=>{
    function scrollListner(){
      setPoint(windowRef.current.scrollY)
    }

    windowRef.current.addEventListener('scroll', scrollListner);
  },[])

  console.log(point)


  return <div>
    <p>{point}</p>
  </div>
}

export default App;
