import { useEffect, useState } from "react";

function App() {
  const [dataState, setDataState] = useState({
    loaded: false,
    data: ''
  });

  function dataLoading() {
    fetch('#')
      .then(response => response.json())
      .then(jsondata => setDataState({
        loaded: true,
        data: jsondata
      }))
      .then(console.log('reloaded'))
      .catch(error => setDataState({
        loaded: false,
        error
      }))
      .finally(setTimeout(() => dataLoading(), 3000))
  }

  useEffect(() => {
    dataLoading();
  }, [])

  return <>
    <p>{dataState.loaded ? dataState.data : 'Please wait...'}</p>
  </>
}

export default App;
