import React, { useState } from "react";
import { useEffect } from "react";

function App() {
  const [color, setColor] = useState('sa');



  useEffect(() => {
    document.body.style.backgroundColor = color;
  }, [color]);

  function click(){
    setColor('red');
  }



  return (
    <div>
      <h1>React App</h1>
      <button onClick={click}>change</button>
    </div>
  );
}

export default App;
