import { useEffect, useState } from "react";

function App() {
  const [text, setText] = useState('react');
  const [num, setNum] = useState(0);

  function add() {
    setText(text + '!');
  }

  function iter(){
    setNum(num+1);
  }



  return <>
    <p onClick={() => add()}>
      {text}
    </p><br/>
    <p onClick={()=> iter()}>{num}</p>
  </>
}

export default App;
