import { useState } from "react";

function App() {

  const [inputState, setInputState] = useState({
    show: false,
    value: '',
  });

  const [deals, setDeals] = useState([]);

  function buttonAdd_onClick_showInput() {
    setInputState({ ...inputState, show: true })
  }

  function input_onChange(e) {
    setInputState({ ...inputState, value: e.target.value })
  }

  function buttonSave_onClick_saveInput() {
    setDeals([...deals, inputState.value])
    setInputState({ ...inputState, show: false });
  }

  return (
    <div /*className={container}*/>
      <div /*className={deals}*/>
        {deals}
      </div>
      <div /*className={work_space}*/>
        <button onClick={() => buttonAdd_onClick_showInput()}>Добавить</button><br />
        {inputState.show
          ? <div>
            <input value={inputState.value} onChange={(e) => input_onChange(e)} /> <br />
            <button onClick={() => buttonSave_onClick_saveInput()}>Сохранить</button>
          </div>
          : null}<br />

      </div>
    </div>
  )
}

export default App;
