import { useEffect, useState } from "react";


function App(){
  const [location, setLocation] = useState({
    loaded: false,
    lat: '',
    lng: ''
  });

  function onSucces(){
    setLocation({
      loaded: true,
      lat: location.lat,
      lng: location.lng
    })
  }

  function onError(error){
    setLocation({
      loaded: false,
      error
    })
  }

  useEffect(()=>{
    navigator.geolocation.getCurrentPosition(onSucces, onError)
  },[])

  return<>

  </>
}

export default App;
