import { useState } from "react";
import {MyContext} from "./MyContext.js";
import Parent from "./Parent.js";
import './styles.css';

function App() {
  const [age, setAge] = useState(50);

  function div2(){
    setAge(age);
  }

  return <MyContext.Provider value={age}>
    <Parent/>
    <button onClick={()=>div2}>div</button>
  </MyContext.Provider>
}

export default App;
