import { useState } from "react";
import { useRef } from "react";

function App() {
  const ref = useRef(null);

  function click() {
    ref.current.focus();
    ref.current.value = '';
  }

  return <>
    <input ref={ref} /><br />
    <button onClick={click}> click </button>
  </>
}

export default App;
