import { useState } from "react";
import { useEffect } from "react";

function App() {
  const [color, setColor] = useState('red');


  function handleClick(e) {
    setColor()
  }

  useEffect(() => {
    window.addEventListener('click', handleClick);
    document.body.style.backgroundColor = color;
  }, [color])

}

export default App;
